import React, { Component } from 'react'
import StatesLived from "../public/data/stateslived.json.json"
import CitiesLived from "../public/data/citieslived.json"
import USData from "../public/data/us-states.json"

import * as d3 from "d3"


class Chart extends Component {

  componentDidMount() {
    console.log("Mounting Chart Component...")
    this.StatesLived = StatesLived;
    this.CitiesLived = CitiesLived;
    this.drawChart();
  }

  drawChart() {

    let json = this.geoData;

    // confirm we've gotten access to the states
    for (var j = 0; j < json.features.length; j++) {
      var jsonState = json.features[j].properties.name;
      console.log(jsonState);
    }
    //Width and height of map
    var width = 960;
    var height = 500;

    // D3 Projection
    var projection = d3
      .geo
      .albersUsa()
      .translate([width / 2, height / 2]) // translate to center of screen
      .scale([1000]); // scale things down so see entire US


    // Define path generator
    var path = d3
      .geo
      .path() // path generator that will convert GeoJSON to SVG paths
      .projection(projection);  // tell path generator to use albersUsa projection

    // Define linear scale for output
    var color = d3
      .scale
      .linear()
      .range(["rgb(213,222,217)", "rgb(69,173,168)", "rgb(84,36,55)", "rgb(217,91,67)"]);

    var legendText = ["Cities Lived", "States Lived", "States Visited", "Nada"];

    //Create SVG element and append map to the SVG
    var svg = d3
      .select("body")
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    // Append Div for tooltip to SVG
    var div = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);






  }

  render() {
    return <div id={"#" + this.props.id}></div>
  }

}

export default Chart
