import React, { Component } from 'react'
import StatesLived from "../public/data/stateslived.json"
import CitiesLived from "../public/data/citieslived.json"
import USData from "../public/data/us-states.json"
import * as d3 from "d3"


class UsMap extends Component {

  componentDidMount() {

    this.statesLived = StatesLived;
    this.citieslived = CitiesLived;
    this.usData = USData;

    this.renderMap();
  }

  renderMap() {

    // just unpacking for tidier variable names downstream
    let usData = this.usData;
    let statesLived = this.statesLived
    let citieslived = this.citieslived

    var width = 960;
    var height = 500;

    var projection = d3
      .geo
      .albersUsa()
      .translate([width / 2, height / 2])
      .scale([1000]);

    var path = d3
      .geo
      .path()
      .projection(projection);

    var color = d3
      .scale
      .linear()
      .range(["rgb(213,222,217)", "rgb(69,173,168)", "rgb(84,36,55)", "rgb(217,91,67)"]);

    var legendText = ["Cities Lived", "States Lived", "States Visited", "Nada"];

    var svg = d3
      .select("body")
      .append("svg")
      .attr("width", width)
      .attr("height", height);

    var div = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    for (var i = 0; i < statesLived.length; i++) {

      var dataState = statesLived[i].state;
      var dataValue = statesLived[i].visited;

      for (var j = 0; j < usData.features.length; j++) {
        var jsonState = usData.features[j].properties.name;

        if (dataState == jsonState) {
          usData.features[j].properties.visited = dataValue;
          break;
        }
      }
    }

    svg.selectAll("path")
      .data(usData.features)
      .enter()
      .append("path")
      .attr("d", path)
      .style("stroke", "#fff")
      .style("stroke-width", "1")
      .style("fill", function (d) {
        let haveVisited = d.properties.visited;
        if (haveVisited) {
          return color(haveVisited);
        } else {
          return "rgb(213,222,217)";
        }
      });

    var legend = d3
      .select("body")
      .append("svg")
      .attr("class", "legend")
      .attr("width", 140)
      .attr("height", 200)
      .selectAll("g")
      .data(color.domain().slice().reverse())
      .enter()
      .append("g")
      .attr("transform", function (d, i) { return "translate(0," + i * 20 + ")"; });

    legend
      .append("rect")
      .attr("width", 18)
      .attr("height", 18)
      .style("fill", color);

    legend
      .append("text")
      .data(legendText)
      .attr("x", 24)
      .attr("y", 9)
      .attr("dy", ".35em")
      .text(function (d) { return d; });

  } // end renderMap()

  render() {
    return <div id={"#" + this.props.id
    } ></div >
  }

}

export default UsMap
